//Declare Variables
let score = 0;
let currentQuestionIndex = 0;
let selectedAnswer = null;

// Function to load the current question
function loadQuestion(index) {
  const questionData = questions[index];

  // Update the question text
  document.getElementById('question-header').textContent = questionData.question;

  // Update the question image
  const questionImage = document.getElementById('question-image');
  questionImage.src = questionData.image;

  // Update the answer buttons
  const buttons = document.querySelectorAll('.answer-btn');
  questionData.answers.forEach((answer, i) => {
    buttons[i].textContent = `${String.fromCharCode(65 + i)}. ${answer}`;
    buttons[i].classList.remove('selected');
    buttons[i].disabled = false;
  });

  // Reset answer selection and disable the submit button
  selectedAnswer = null;
  document.getElementById('submit-btn').disabled = true;
}

// Event listener for answer selection
document.querySelectorAll('.answer-btn').forEach(button => {
  button.addEventListener('click', (event) => {
    // Deselect other buttons and select the clicked one
    document.querySelectorAll('.answer-btn').forEach(btn => btn.classList.remove('selected'));
    event.target.classList.add('selected');
    selectedAnswer = event.target.textContent.split('. ')[1];

    // Enable the submit button
    document.getElementById('submit-btn').disabled = false;
  });
});

// Event listener for the submit button
document.getElementById('submit-btn').addEventListener('click', () => {
  // Check if the selected answer is correct
  if (selectedAnswer === questions[currentQuestionIndex].correctAnswer) {
    score++;
  }

  // Move to the next question or end the quiz
  currentQuestionIndex++;
  if (currentQuestionIndex < questions.length) {
    loadQuestion(currentQuestionIndex);
  } else {
    document.getElementById('question-header').textContent = `Quiz Finished! Your score: ${score}/${questions.length}`;
    document.getElementById('question-image').style.display = 'none';
    document.querySelectorAll('.answer-btn').forEach(btn => btn.style.display = 'none');
    document.getElementById('submit-btn').style.display = 'none';
  }
});

// Initialize the quiz
loadQuestion(currentQuestionIndex);
